import 'package:flutter/material.dart';

class Producto {
  final String nombre;
  final String imagenUrl;
  final double precio;

  Producto({
    required this.nombre,
    required this.imagenUrl,
    required this.precio,
  });
}

class Oferta extends StatelessWidget {
  final List<Producto> productos = [
    Producto(
      nombre: 'Vestido De Cuello De Tortuga Con Caída De Tela',
      imagenUrl:
          'data:image/jpeg;base64,',
      precio: 9.390,
    ),
    Producto(
      nombre: 'Vestido De Cuello De Muñeca Acanalado Para Mujer',
      imagenUrl:
          'data:image/jpeg;base64,',
      precio: 7.974,
    ),
    Producto(
      nombre: '2 Piezas Bandeja Redonda Para Freidora De Aire De Silicona',
      imagenUrl:
          'data:image/jpeg;base64,',
      precio: 2.874,
    ),
    Producto(
      nombre: 'THE POWERPUFF GIRLS X SHEIN Botella de agua fría',
      imagenUrl:
          'data:image/jpeg;base64,',
      precio: 5.537,
    ),
    Producto(
      nombre: '1 pieza Alfombrilla de escritorio unicolor',
      imagenUrl:
          'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQfb6dwSFmOIGqPzLNa_5Bje9WResCukutFdw&s',
      precio: 6.390,
    ),
  ];

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        title: Text('Oferta'),
        backgroundColor: Colors.yellow, // Cambiar el color del app bar a negro
      ),
      body: ListView.builder(
        itemCount: productos.length,
        itemBuilder: (context, index) {
          return Container(
            margin: EdgeInsets.symmetric(vertical: 8, horizontal: 16),
            padding: EdgeInsets.all(8),
            decoration: BoxDecoration(
              color:
                  Colors.white, // Fondo blanco para cada elemento de la lista
              borderRadius: BorderRadius.circular(16), // Bordes redondeados
              boxShadow: [
                BoxShadow(
                  color: Colors.grey.withOpacity(0.5),
                  spreadRadius: 2,
                  blurRadius: 5,
                  offset: Offset(0, 3), // Cambiar la posición de la sombra
                ),
              ],
            ),
            child: ListTile(
              leading: ClipRRect(
                borderRadius: BorderRadius.circular(8),
                child: Image.network(
                  productos[index].imagenUrl,
                  width: 80,
                  height: 80,
                  fit: BoxFit.cover,
                ),
              ),
              title: Text(
                productos[index].nombre,
                style: TextStyle(
                  fontWeight: FontWeight.bold,
                  fontSize: 18,
                ),
              ),
              subtitle: Text(
                '\$${productos[index].precio.toStringAsFixed(2)}',
                style: TextStyle(
                  color: Colors.red, // Cambiar el color del precio a rojo
                  fontSize: 16,
                ),
              ),
              trailing: Icon(
                Icons.arrow_forward_ios,
                color: Colors.black,
              ),
              onTap: () {
                // Acción al hacer clic en un producto
              },
            ),
          );
        },
      ),
    );
  }
}

void main() {
  runApp(MaterialApp(
    home: Oferta(),
  ));
}
